     public boolean verificaDados() {

        if (!jTNome.getText().equals("") && !jTEmail.getText().equals("") && !jTSenha.getText().equals("")  ) {
            return true;
        } else {
            JOptionPane.showMessageDialog(null, "os campos nome, matricula e senha nao podem ser nulos!!");
            return false;
        }
    }
    public void cadastro() {

        if( jCTipo.getSelectedItem().equals("Cliente") ){
            Costumer.create(jTNome.getText(), jTEmail.getText(), "", jTSenha.getText());
        }
        else{
            Hostess.create(jTNome.getText(), jTEmail.getText(), "", jTSenha.getText());
        }
    }
